--update spms_device(GW) storage--
update spms_device sd1 inner join (select id from spms_device where id 
in(select sd.id from spms_user su,spms_device sd where su.gw_id=sd.id)) child_table 
on sd1.id=child_table.id set storage ='5';
--update spms_device(AC) storage--

update spms_device sd1 inner join (select id from spms_device where id 
in(select sd.id from spms_user_product_binding supb,spms_device sd where supb.device_id = sd.id )) child_table 
on sd1.id=child_table.id set storage ='5';

--批量更新spms_product表中的时间--
update spms_product set subscribeDate=date_format('2015-01-01','%Y-%m-%d') ,
activateDate=date_format('2015-01-01','%Y-%m-%d'),expireDate=date_format('2015-04-01','%Y-%m-%d')
 where id in(select t.id from (select * from spms_product  order by id limit 1,25)as t)
 --批量更新spms_user中的balance和credit--
 update spms_user su inner join (select `sp`.`user_id` AS `user_id`,`spt`.`countRmb` AS `countRmb` from (`spms_product` `sp` join `spms_product_type` `spt`) where (`sp`.`type_id` = `spt`.`id`)) rt on rt.user_id = su.id set su.balance=rt.countRmb ,credit=rt.countRmb where  su.id = rt.user_id ;
